import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { Sidebarinfo } from "../App";

function CalendarScheduler() {
  const [year, setYear] = useState(new Date().getFullYear());
  const [month, setMonth] = useState(new Date().getMonth());
  const [day, setDay] = useState(1);
  const [calendarMatrix, setCalendarMatrix] = useState([]);

  const { hidesidebar } = useContext(Sidebarinfo);

  useEffect(() => {
    const firstdayofthemonth = new Date(year, month, 1).getDay(); // Ayın ilk basladigi gunu veriyor 0 pazar
    const weekdays = ["Pazartesi", "Salı", "Çarşamba", "Perşembe", "Cuma", "Cumartesi", "Pazar"];
    const howmanydaysinmonth = new Date(year, month + 1, 0).getDate(); // Ayın kaç gün olduğunu veriyor
    const howmanydaysinpreviousmonth = new Date(year, month, 0).getDate(); // gecen ayın kaç gün olduğunu veriyor

    let calendarMatrix = new Array(6);

    let firstdate = 0; // burada yanlis var düzeltilmesi lazım. Ayın sayısı kadar dönderiyor

    for (let i = 0; i < calendarMatrix.length; i++) {
      calendarMatrix[i] = new Array(7);

      for (let j = 0; j < calendarMatrix[i].length; j++) {
        if (firstdate < howmanydaysinmonth) {
          if (firstdayofthemonth > 0) {
            calendarMatrix[i][j] = getDays(year, month - 1, howmanydaysinpreviousmonth - firstdayofthemonth + 1);
            firstdate = -1;
          } else {
            calendarMatrix[i][j] = getDays(year, month, day + firstdate);
          }
        }
        firstdate++;
        firstdayofthemonth--;
      }
    }
    setCalendarMatrix(calendarMatrix);
  }, [year, month]);

  function getDays(takenyear, takenmonth, takenday) {
    const date = new Date(takenyear, takenmonth, takenday);
    const daysYear = date.getFullYear();
    const daysMonth = date.getMonth();
    const daysNumber = date.getDate();
    const weekday = date.getDay();
    return {
      daysYear,
      daysMonth,
      daysNumber,
      weekday,
    };
  }

  return (
    <div
      className="mainboard"
      style={hidesidebar ? mainboardextend : mainboard}
    >
      <div className="scheduler-container">
        {" "}
        <ul className="scheduler">
          {weekdays.map((day, index) => {
            return (
              <li key={index}>
                <h5>{day}</h5>
              </li>
            );
          })}
        </ul>
        <ul className="scheduler-weekdays-numbers">
          {calendarMatrix.map((calendarMatrixdays, index) => {
            return calendarMatrixdays.map((matrixday, index) => {
              return (
                <li>
                  {" "}
                  <h5>Gün{matrixday.daysNumber} </h5>
                  <h5> Haftanın günü{matrixday.weekday} </h5>
                  <h5>Ay{matrixday.daysMonth} </h5>
                  <h5>Yıl{matrixday.daysYear}</h5>
                </li>
              );
            });
          })}
        </ul>
      </div>{" "}
    </div>
  );
}

export default CalendarScheduler;
