import React, { useState } from "react";
import { useContext } from "react";
import { Sidebarinfo } from "../App";

function Scheduler() {
  const mainboardextend = {
    backgroundColor: "#CCE2FF",
    marginLeft: 75,
    top: 50,
  };
  const mainboard = {
    backgroundColor: "#CCE2FF",
    marginLeft: 250,
    top: 50,
  };
  const { hidesidebar, setHidesidebar } = useContext(Sidebarinfo);

  const currentYear = new Date().getFullYear();
  const SelectedYear = 2020;
  const [year, setYear] = useState(2024);

  function getFebdays() {
    return currentYear % 4 === 0 ? 29 : 28;
  }

  const NofDays = [31, getFebdays(), 31, 30, 31, 30, 31, 30, 31, 30, 31, 30];

  const weekdays = [
    "Pazartesi",
    "Salı",
    "Çarşamba",
    "Perşembe",
    "Cuma",
    "Cumartesi",
    "Pazar",
  ];

  useState(() => {}, [year]);

  return (
    <div
      className="mainboard"
      style={hidesidebar ? mainboardextend : mainboard}
    >
      <div>
        <input
          type="number"
          id="year"
          min="2000"
          max="2100"
          onChange={(e) => setYear(e.target.value)}
        ></input>
        <ul className="Scheduler">
          year{year}
          days{" "}
          {NofDays.map((number, index) => {
            
           return <h1>{number}</h1>;
          })}
          {weekdays.map((day, index) => {
            return (
              <li>
                {day}
                <ul>{weekdays.map((numberofday, index) => {})}</ul>
              </li>
            );
          })}
        </ul>
      </div>{" "}
    </div>
  );
}

export default Scheduler;
