import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { Sidebarinfo } from "../App";

function Scheduler() {
  const mainboardextend = {
    backgroundColor: "#CCE2FF",
    marginLeft: 75,
    top: 50,
  };
  const mainboard = {
    backgroundColor: "#CCE2FF",
    marginLeft: 250,
    top: 50,
  };

  const currentYear = new Date().getFullYear();
  const currentmonth = new Date().getMonth();

  const { hidesidebar, setHidesidebar } = useContext(Sidebarinfo);
  const [year, setYear] = useState(2024);
  const [month, setMonth] = useState(0); // 0 ocak
  const [day, setDay] = useState(1);
  const [alldays, setAlldays] = useState([]);
  const [pageupdated, setPageupdated] = useState(false);

  const [selectedperiod, setSelectedperiod] = useState(1);

  const selecteddate = `${year}${month}${day}`;
  let firstdayofmonth = new Date(year, month, 1).getDay(); // Ayın hangi gün ile başladığını veriyor 0-6 arası. 0 Pazar günü.
  let lastdateofmonth = new Date(year, month + 1, 0).getDate(); // Ayın kaç gün olduğunu veriyor

  function getFebdays() {
    return year % 4 === 0 ? 29 : 28;
  }

  const NofDays = [31, getFebdays(), 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

  const weekdays = [
    "Pazartesi",
    "Salı",
    "Çarşamba",
    "Perşembe",
    "Cuma",
    "Cumartesi",
    "Pazar",
  ];
  const months = [
    "Ocak",
    "Şubat",
    "Mart",
    "Nisan",
    "Mayıs",
    "Haziran",
    "Temmuz",
    "Ağustos",
    "Eylül",
    "Ekim",
    "Kasım",
    "Aralık",
  ];

  const getNof = async () => {
    const result = await getNofdays();
    console.log(result)
  };

  function getNofdays() {
    return new Promise((resolve, reject) => {
      let daysinmonth = [];
      setDaysinmonth([]);
      let firstdayofthemonth = new Date(year, month, 1).getDay();
      for (let i = 1; i <= NofDays[month]; i++) {
        for (
          let i = firstdayofthemonth !== 0 ? firstdayofthemonth : 7;
          i > 1;
          i--
        ) {
          daysinmonth.push(NofDays[month] - i + 1);
        }
  
        daysinmonth.push(i);
      }
      resolve(daysinmonth);
    });
  }
  const hours = [
    "09.00",
    "10.00",
    "11.00",
    "12.00",
    "13.00",
    "14.00",
    "15.00",
    "16.00",
    "17.00",
    "18.00",
    "19.00",
    "20.00",
    "21.00",
    "22.00",
  ];

  useEffect(() => {
    getNof();
    console.log("ilkgun", firstdayofmonth);
    console.log("songun", lastdateofmonth);
    console.log("periyot", selectedperiod);
    console.log("ay", month);
    console.log("aygunleri", daysinmonth);
  }, [pageupdated]);

  return (
    <div
      className="mainboard"
      style={hidesidebar ? mainboardextend : mainboard}
    >
      {" "}
      <div className="scheduler-container">
        <div className="scheduler-datepicker">
          <section>
            <button
              onClick={() => {
                getNofdays();
                setPageupdated(!pageupdated);
              }}
            >
              bos beles
            </button>{" "}
            <button
              onClick={() => {
                month > 0
                  ? setMonth((PrevMonth) => PrevMonth - 1)
                  : setMonth(11);
              }}
            >
              <span>
                <i class="fa fa-chevron-left" aria-hidden="true"></i>
              </span>
            </button>
            <button
              onClick={() => {
                month < 11
                  ? setMonth((PrevMonth) => PrevMonth + 1)
                  : setMonth(0);
                console.log("ay", month);
              }}
            >
              <span>
                <i class="fa fa-chevron-right" aria-hidden="true"></i>
              </span>
            </button>{" "}
          </section>

          <section>
            {" "}
            {months[month]} {daysinmonth[0]} , {year}
          </section>
          <section>
            <button
              onClick={() => {
                setSelectedperiod(1);
              }}
            >
              {" "}
              Gün
            </button>
            <button
              onClick={() => {
                setSelectedperiod(2);
              }}
            >
              {" "}
              Hafta
            </button>
            <button
              onClick={() => {
                setSelectedperiod(3);
              }}
            >
              {" "}
              Ay
            </button>
          </section>
        </div>

        <ul className="scheduler">
          {weekdays.map((day, index) => {
            return (
              <li>
                <h5>{day}</h5>
              </li>
            );
          })}
        </ul>
        <ul className="scheduler-weekdays-numbers">
          {daysinmonth.map((daynumber, index) => {
            return <li>{daynumber}</li>;
          })}
        </ul>
      </div>
    </div>
  );
}

export default Scheduler;
