import React, { useState } from "react";
import { useContext } from "react";
import { Sidebarinfo } from "../App";

function Scheduler() {
  const mainboardextend = {
    backgroundColor: "#CCE2FF",
    marginLeft: 75,
    top: 50,
  };
  const mainboard = {
    backgroundColor: "#CCE2FF",
    marginLeft: 250,
    top: 50,
  };

  const currentYear = new Date().getFullYear();
  const currentmonth = new Date().getMonth();

  const { hidesidebar, setHidesidebar } = useContext(Sidebarinfo);
  const [year, setYear] = useState();
  const [month, setMonth] = useState(0); // 0 ocak
  const [day, setDay] = useState(1);
  const [pageupdated, setPageupdated] = useState(false);

  const selecteddate = `${year}${month}${day}`;
  let firstdayofmonth = new Date(year, month, 1).getDay(); // Ayın hangi gün ile başladığını veriyor 0-6 arası. 0 Pazar günü.
  let lastdateofmonth = new Date(year, month + 1, 0).getDate(); // Ayın kaç gün olduğunu veriyor

  function getFebdays() {
    return year % 4 === 0 ? 29 : 28;
  }

  const NofDays = [31, getFebdays(), 31, 30, 31, 30, 31, 30, 31, 30, 31, 30];

  const weekdays = [
    "Pazar",
    "Pazartesi",
    "Salı",
    "Çarşamba",
    "Perşembe",
    "Cuma",
    "Cumartesi",
  ];
  const months = [
    "Ocak",
    "Şubat",
    "Mart",
    "Nisan",
    "Mayıs",
    "Haziran",
    "Temmuz",
    "Ağustos",
    "Eylül",
    "Ekim",
    "Kasım",
    "Aralık",
  ];

  let daysinmonth = [];
  function getNofdays() {
    let firstdayoftheyear = new Date(year, 0, 1).getDay();
    console.log("firstdayof2024", firstdayoftheyear);
    for (let i = 0; i < firstdayoftheyear; i++) {
      daysinmonth.push("");
    }
    months.map((month, index) => {
      for (let i = 1; i <= NofDays[index]; i++) {
        daysinmonth.push(i);
      }
    });

    return daysinmonth;
  }

  const hours = [
    "09.00",
    "10.00",
    "11.00",
    "12.00",
    "13.00",
    "14.00",
    "15.00",
    "16.00",
    "17.00",
    "18.00",
    "19.00",
    "20.00",
    "21.00",
    "22.00",
  ];

  useState(() => {
    getNofdays();
    console.log("ilkgun", firstdayofmonth);
    console.log("songun", lastdateofmonth);
  }, [year, pageupdated]);

  return (
    <div
      className="mainboard"
      style={hidesidebar ? mainboardextend : mainboard}
    >
      <div>
        {" "}
        <input
          type="number"
          id="year"
          min="2000"
          max="2100"
          onChange={(e) => {
            setYear(e.target.value);
            setPageupdated(!pageupdated);
          }}
        ></input>
        {year && (
          <ul className="scheduler">
            {weekdays.map((day, index) => {
              return (
                <li>
                  <div className="scheduler-weekdays">
                    {" "}
                    <h3> {day}</h3>
                    <h4>{daysinmonth[index + 7]}</h4>
                    {"  "}
                  </div>

                  <ul className="scheduler-cells">
                    {hours.map((hour, index) => {
                      return <li></li>;
                    })}
                  </ul>
                </li>
              );
            })}
          </ul>
        )}
        <ul className="scheduler-hours">
          {hours.map((hour, index) => {
            return <li>{hour}</li>;
          })}
        </ul>
      </div>{" "}
    </div>
  );
}

export default Scheduler;
