import React, { useEffect } from "react";
import { useState } from "react";
import Axios from "axios";
import { useLocation } from "react-router-dom";
import { useContext } from "react";
import { Sidebarinfo } from "../App";

function Preregistratedpatient() {
  const mainboardextend = {
    height: "35%",
    backgroundColor: "#CCE2FF",
    marginLeft: 50,
    top: 50,
    flex: 1,
  };
  const mainboard = {
    height: "35%",
    backgroundColor: "#CCE2FF",
    marginLeft: 250,
    top: 50,
    flex: 1,
  };

  const { hidesidebar, setHidesidebar } = useContext(Sidebarinfo);
  const localation = useLocation();
  const patientinformation = localation.state.onkayitlihasta_unique_id;
  const { URL } = useContext(Sidebarinfo);

  const [preregisteredpatient, setPreregisteredpatient] = useState([]);
  const [showConfirmationbox, setShowConfirmationbox] = useState(false);
  const [registrationlaststatus, setRegistrationlaststatus] = useState("");
  const [confirmationstatus, setConfirmationstatus] = useState(false);

  useEffect(() => {
    console.log(patientinformation);
    const fetchData = async () => {
      const result = await Axios.get(`${URL}/getPatientTreatment`, {
        params: {
          patientinformation,
        },
      });
      setPreregisteredpatient(result.data);
    };
    fetchData();
  }, [registrationlaststatus]);
  const cleanup = () => {
    setRegistrationlaststatus("");
  };

  function updatestatus(status, patient) {
    console.log(status, patient);

    Axios.post(`${URL}/updateregisterstatus`, {
      patient: patient,
      status: status,
    }).then((res) => {
      alert("Ön Kayıt Durumu Güncellendi");
      setRegistrationlaststatus(patient, status);
    });
  }

  return (
    <div
      className="mainboard"
      style={hidesidebar ? mainboardextend : mainboard}
    >
      <div>
        {" "}
        Ön kayıtlı hasta {localation.state.onkayitlihasta_unique_id}
        {/* 
      {showConfirmationbox && (
        <div className="confirmationbox">
          <button
            onClick={() => {
              setConfirmationstatus(true);
              setShowConfirmationbox(false);
            }}
          >
            Onayla
          </button>
          <button
            onClick={() => {
              setConfirmationstatus(false);
              setShowConfirmationbox(false);
            }}
          >
            Vazgeç
          </button>
        </div>
      )} */}{" "}
        <button
          style={{ backgroundColor: "green" }}
          onClick={() => {
            updatestatus(2, localation.onkayitlihasta_unique_id);
          }}
        >
          <span>
            <i class="fa fa-check" aria-hidden="true"></i>
          </span>
        </button>
        <button
          style={{ backgroundColor: "red" }}
          onClick={() => {
            updatestatus(3, localation.onkayitlihasta_unique_id);
          }}
        >
          {" "}
          <span>
            {" "}
            <i class="fa fa-times" aria-hidden="true"></i>
          </span>
        </button>
        <button
          style={{ backgroundColor: "gray" }}
          onClick={() => {
            updatestatus(1, localation.onkayitlihasta_unique_id);
          }}
        >
          <span>
            <i class="fa fa-hourglass-half" aria-hidden="true"></i>
          </span>
        </button>
      </div>
    </div>
  );
}

export default Preregistratedpatient;
