  const { initialweek } = useContext(Sidebarinfo); // Bugünün haftasını belirliyor

  const today = new Date();
  const currentYear = today.getFullYear();
  const currentMonth = today.getMonth();
  const currentDay = today.getDate();

  const [fullmatrix, setFullmatrix] = useState([]);
  const [events, setEvents] = useState([]);
  const [year, setYear] = useState(currentYear);
  const [month, setMonth] = useState(parseInt("08")); // 0 ocak
  const [day, setDay] = useState(currentDay);
  const [dayindexinweek, setDayindexinweek] = useState(initialdayindex);
  const [week, setWeek] = useState(initialweek);
  const [selectedperiod, setSelectedperiod] = useState(2); // 1 gün  2 hafta 3 ay
 
  const a = [
    {
      name: "Hasan Kurtini",
      phone: "+905352789299",
      date: "2023-05-01",
      start_time: "10:00",
      end_time: "11:30",
    },
    {
      name: "Hasan Kurtini",
      phone: "+905352789299",
      date: "2023-05-02",
      start_time: "09:00",
      end_time: "11:30",
    },
    {
      name: "Hasan Kurtini",
      phone: "+905352789299",
      date: "2023-05-04",
      start_time: "11:00",
      end_time: "11:30",
    },
    {
      name: "Hasan Kurtini",
      phone: "+905352789299",
      date: "2023-05-05",
      start_time: "12:00",
      end_time: "11:30",
    },
  ];

  const months = [
    "Ocak",
    "Şubat",
    "Mart",
    "Nisan",
    "Mayıs",
    "Haziran",
    "Temmuz",
    "Ağustos",
    "Eylül",
    "Ekim",
    "Kasım",
    "Aralık",
  ];
  const weekdays = [
    "Pazartesi",
    "Salı",
    "Çarşamba",
    "Perşembe",
    "Cuma",
    "Cumartesi",
    "Pazar",
  ];

  const hours = [
    "00:00",
    "00:30",
    "01:00",
    "01:30",
    "02:00",
    "02:30",
    "03:00",
    "03:30",
    "04:00",
    "04:30",
    "05:00",
    "05:30",
    "06:00",
    "06:30",
    "07:00",
    "07:30",
    "08:00",
    "08:30",
    "09:00",
    "09:30",
    "10:00",
    "10:30",
    "11:00",
    "11:30",
    "12:00",
    "12:30",
    "13:00",
    "13:30",
    "14:00",
    "14:30",
    "15:00",
    "15:30",
    "16:00",
    "16:30",
    "17:00",
    "18:00",
    "18:30",
    "19:00",
    "19:30",
    "20:00",
    "20:30",
    "21:00",
    "21:30",
    "22:00",
    "22:30",
    "23:00",
    "23:30",
  ];

  function getCalendarMatrixDays() {
    let firstweekday = new Date(year, month, 1).getDay(); // Ayin ilk basladigi gunu veriyor 0 pazar
    let firstweekdayofthemonth = firstweekday !== 0 ? firstweekday - 1 : 6;
    let howmanydaysinmonth = new Date(year, month + 1, 0).getDate(); // Ayın kaç gün olduğunu veriyor
    let firstdayofthecurrentmonth = 1; // burada yanlis var düzeltilmesi lazım. Ayın sayısı kadar dönderiyor
    let diffbetweenmatrixandmonth = 42 - howmanydaysinmonth;
    let nextmonthsdays = 1;
    let howmanydaysinpreviousmonth = getPreviousdays(year, month, 1);
