      const howmanydaysinpreviousmonth = new Date(
        takenyear,
        takenmonth,
        0
      ).getDate(); // gecen ayın kaç gün olduğunu veriyor

      console.log("oncekiay", howmanydaysinpreviousmonth);
      return howmanydaysinpreviousmonth;
    }

