        else if (firstdayofthecurrentmonth <= howmanydaysinmonth) {
          calendarMatrix[i][j] = getDays(
            year,
            month,
            firstdayofthecurrentmonth
          );
          firstdayofthecurrentmonth++;
        }
        // gelecek ayin gunlerini alir ve 7 x 6 lik matrixi tamamlar
        else if (diffbetweenmatrixandmonth >= 0) {
          calendarMatrix[i][j] = getDays(year, month + 1, nextmonthsdays);

          diffbetweenmatrixandmonth--;
          nextmonthsdays++;
        }
      }
    }

    let weekrow = calendarMatrix.filter((periodday, index) => {
      if (index === week) {
        return periodday;
      }
    });

    let weekday = weekrow.map((wdi, index) => {
      return wdi.filter((wkday, index) => {
        if (index === dayindexinweek) {
          return wkday;
        }
      });
    });

    return selectedperiod === 1 ? weekday : selectedperiod === 2 ? weekrow : 0;
  }

  // sonraki ay

  const nextDate = () => {
    switch (selectedperiod) {
      case 1:
        if (dayindexinweek === 6) {
          setDayindexinweek(0);

          if (week === 5) {
            if (month === 11) {
              setYear((year) => year + 1);
              setMonth(0);
              setWeek(0);
              setDayindexinweek(0);
            } else {
              setWeek(0);
              setMonth((month) => month + 1);
            }
          } else {
            setWeek((week) => week + 1);
          }
        } else {
          setDayindexinweek((dayindexinweek) => dayindexinweek + 1);
        }
        break;
      case 2:
        if (week === 5) {
          setWeek((week) => 0);
          if (month === 11) {
            setMonth((month) => 0);
            setYear((year) => year + 1);
          } else {
            setMonth((month) => month + 1);
          }
        } else {
          setWeek((week) => week + 1);
        }
        break;
      case 3:
        setMonth(month + 1);
        break;
      default:
        break;
    }
  }; // Önceki ay
  const prevDate = () => {
    switch (selectedperiod) {
      case 1:
        if (dayindexinweek === 0) {
          setDayindexinweek(6);

          if (week === 0) {
            if (month === 0) {
              setYear((year) => year - 1);
              setMonth(11);
              setWeek(5);
              setDayindexinweek(6);
            } else {
              setWeek(5);
              setMonth((month) => month - 1);
            }
          } else {
            setWeek((week) => week - 1);
          }
        } else {
          setDayindexinweek((dayindexinweek) => dayindexinweek - 1);
        }
        break;
      case 2:
        if (week === 0) {
          setWeek(5);
          setMonth(month - 1);
          if (month === 0) {
            setYear(year - 1);
            setMonth(11);
          }
        } else {
          setWeek(week - 1);
        }
        break;
      case 3:
        setMonth(month - 1);

        break;
      default:
        break;
    }
  };
  function setdateTotoday() {
    setYear(currentYear);
    setMonth(currentMonth);
    setDayindexinweek(initialdayindex);
    setWeek(initialweek);
  }

  useEffect(() => {
    const fetchData = () => {
      const result = getCalendarMatrixDays();

      setFullmatrix(result);
    };
    const getEvents = async () => {
      const result = await Axios.get(`${URL}/GetPreregistrationsList`, {
        params: {
          // preregistrationstatus,
          // registerendtime,
          // registerstarttime,
        },
      });

      setEvents(result.data);
    };

    fetchData();
    getEvents();

    console.log("eventlist", date, year, month, day);
  }, [selectedperiod, week,day,]);

 

  return (
    <div className="scheduler-container">
      {" "}
      <div className="scheduler-datepicker">
        <section>
          <button
            onClick={() => {
              prevDate();
            }}
          >
            <span>
              <i class="fa fa-chevron-left" aria-hidden="true"></i>
            </span>
          </button>{" "}
          <button
            onClick={() => {
              setdateTotoday();
            }}
          >
            Bugün
          </button>{" "}
          <button
            onClick={() => {
              nextDate();
            }}
          >
            <span>
              <i class="fa fa-chevron-right" aria-hidden="true"></i>
            </span>
          </button>{" "}
          {months[month]} {year} {week} {dayindexinweek}
        </section>

        <section></section>
        <section>
          <button
            onClick={() => {
              setSelectedperiod(1);
            }}
          >
            {" "}
            Gün
          </button>
          <button
            onClick={() => {
              setSelectedperiod(2);
            }}
          >
            {" "}
            Hafta
          </button>
          <button
            onClick={() => {
              setSelectedperiod(3);
            }}
          >
            {" "}
            Ay
          </button>
        </section>
      </div>
      {/*  matrix burada gösteriyoruz  */}
      <ul className="scheduler-matrix-days">
        <li>Saat/Gün</li>
        {fullmatrix.map((calendarMatrixdays, index) => {
          return calendarMatrixdays.map((matrixday, index) => {
            return (
              <li
                onClick={() => {
                  console.log("dayss", matrixday);
                }}
              >
                <div>
                  {" "}
                  <h5>
                    {
                      weekdays[
                        matrixday.weekday !== 0 ? matrixday.weekday - 1 : 6
                      ]
                    }{" "}
                  </h5>
                </div>
                <div className="weekdaysdetail">
                  {" "}
                  <h5>{matrixday.daysNumber} </h5>
                  {/* <h5> aa{matrixday.weekday} </h5> */}
                  <h5>{months[matrixday.daysMonth]} </h5>
                  <h5>{matrixday.daysYear}</h5>{" "}
                </div>
              </li>
            );
          });
        })}
      </ul>{" "}
      <div className="scheduler-matrix-dayswithhours">
        <ul className="scheduler-matrix-dayswithhours-list">
          {" "}
          <li className="scheduler-matrix-dayswithhours-display">
            {" "}
            <ul className="hoursdisplay">
              {hours.map((hour, a) => {
                return <li onClick={() => {}}>{hour}</li>;
              })}
            </ul>
          </li>
          {fullmatrix.map((calendarMatrixdays, index) => {
            return calendarMatrixdays.map((matrixday, index) => {
              return (
                <li>
                  {" "}
                  <ul className="scheduler-hours">
                    {hours.map((hour, a) => {
                      const eventsday = String(
                        matrixday.daysNumber + 1
                      ).padStart(2, "0");
                      const eventsmonth = String(
                        matrixday.daysMonth + 1
                      ).padStart(2, "0");
                      const eventsyear = matrixday.daysYear;
                      const eventsdate = `${eventsday}-${eventsmonth}-${eventsyear}`;
                      return (
                        <li onClick={() => {}}>
                          {events.map((event, index) => {
                            // console.log(
                            //   event.baslangic_saati,
                            //   hour,
                            //   event.randevu_gun,
                            //   eventsdate
                            // );

                            if (
                              event.baslangic_saati === hour &&
                              event.randevu_gun === eventsdate
                            ) {
                              console.log("ss", event.ad_soyad);
                              return <h5>{event.ad_soyad}</h5>;
                            }
                          })}
                        </li>
                      );
                    })}
                  </ul>
                </li>
              );
            });
          })}
        </ul>
      </div>
    </div>
  );
}

