import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { Sidebarinfo } from "../App";

function Scheduler() {
  const mainboardextend = {
    backgroundColor: "#CCE2FF",
    marginLeft: 75,
    top: 50,
  };
  const mainboard = {
    backgroundColor: "#CCE2FF",
    marginLeft: 250,
    top: 50,
  };

  const today = new Date();
  const currentYear = today.getFullYear();
  const currentMonth = today.getMonth() + 1;
  const currentDay = today.getDate();

  const { date } = useContext(Sidebarinfo);
  const { hidesidebar, setHidesidebar } = useContext(Sidebarinfo);

  const [year, setYear] = useState(currentYear);
  const [month, setMonth] = useState(currentMonth); // 0 ocak
  const [day, setDay] = useState(currentDay);

  const [daysinmonth, setDaysinmonth] = useState([]);
  const [pageupdated, setPageupdated] = useState(false);
  const [selectedperiod, setSelectedperiod] = useState(2); // 1 gün  2 hafta 3 ay

  const todaysdate = `${year}-${month + 1}-${day}`;

  let firstdayofmonth = new Date(year, month, 1).getDay(); // Ayın hangi gün ile başladığını veriyor 0-6 arası. 0 Pazar günü.
  let lastdateofmonth = new Date(year, month + 1, 0).getDate(); // Ayın kaç gün olduğunu veriyor

  function getFebdays() {
    return year % 4 === 0 ? 29 : 28;
  }

  const NofDays = [31, getFebdays(), 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

  const weekdays = [
    "Pazartesi",
    "Salı",
    "Çarşamba",
    "Perşembe",
    "Cuma",
    "Cumartesi",
    "Pazar",
  ];
  const months = [
    "Ocak",
    "Şubat",
    "Mart",
    "Nisan",
    "Mayıs",
    "Haziran",
    "Temmuz",
    "Ağustos",
    "Eylül",
    "Ekim",
    "Kasım",
    "Aralık",
  ];
  function setdateTotoday() {
    setMonth(currentMonth);
    setYear(currentYear);
    setDay(currentDay);
  }

  function handledatechange(value) {
    switch (selectedperiod) {
      case 1:
        setDay(day < NofDays[month] && day > 0 ? day + value : "");
        break;

      case 2:
        if (value === +1) {
          setMonth(month < 11 ? month + value : 0);
        } else setMonth(month > 0 ? month + value : 11);

        break;
    }

    console.log(day);
  }

  function getNofdays() {
    let daysinmonth = [];
    let firstdayofthemonth = new Date(year, month, 1).getDay();
    for (
      let i = firstdayofthemonth !== 0 ? firstdayofthemonth : 7;
      i > 1;
      i--
    ) {
      daysinmonth.push(NofDays[month] - i + 1);
    }
    for (let i = 1; i <= NofDays[month]; i++) {
      daysinmonth.push(i);
    }
    return daysinmonth;
  }
  const hours = [
    "09.00",
    "10.00",
    "11.00",
    "12.00",
    "13.00",
    "14.00",
    "15.00",
    "16.00",
    "17.00",
    "18.00",
    "19.00",
    "20.00",
    "21.00",
    "22.00",
  ];

  useEffect(() => {
    const fetchData = () => {
      const result = getNofdays();
      setDaysinmonth(result);
    };
    fetchData();

    console.log("tarih", date);
  }, [month, pageupdated, selectedperiod, day]);

  return (
    <div
      className="mainboard"
      style={hidesidebar ? mainboardextend : mainboard}
    >
      {" "}
      <div className="scheduler-container">
        <div className="scheduler-datepicker">
          <section>
            <button
              onClick={() => {
                handledatechange(-1);
              }}
            >
              <span>
                <i class="fa fa-chevron-left" aria-hidden="true"></i>
              </span>
            </button>{" "}
            <button
              onClick={() => {
                setdateTotoday();
              }}
            >
              Bugün
            </button>{" "}
            <button
              onClick={() => {
                handledatechange(+1);
              }}
            >
              <span>
                <i class="fa fa-chevron-right" aria-hidden="true"></i>
              </span>
            </button>{" "}
          </section>

          <section>
            {`bugunn ${todaysdate}`}= {months[month]} {daysinmonth[0]} , {year}
          </section>
          <section>
            <button
              onClick={() => {
                setSelectedperiod(1);
              }}
            >
              {" "}
              Gün
            </button>
            <button
              onClick={() => {
                setSelectedperiod(2);
              }}
            >
              {" "}
              Hafta
            </button>
            <button
              onClick={() => {
                setSelectedperiod(3);
              }}
            >
              {" "}
              Ay
            </button>
          </section>
        </div>

        <ul className="scheduler">
          {weekdays.map((day, index) => {
            return (
              <li>
                <h5>{day}</h5>
              </li>
            );
          })}
        </ul>
        <ul className="scheduler-weekdays-numbers">
          {daysinmonth.map((daynumber, index) => {
            return <li>{daynumber}</li>;
          })}
        </ul>
      </div>
    </div>
  );
}

export default Scheduler;
